<?php
namespace modules\supersearch\variables;

use Craft;
use craft\helpers\UrlHelper;
use craft\helpers\Template as TemplateHelper;

class SuperSearchVariable {

    public function getResultLink($element) {
      $href = '#';

      if ($element->section->handle == 'games') {
        $href = UrlHelper::siteUrl('our-games#game-' . $element->id);
      } elseif ($element->section->handle == 'staff') {
        $href = UrlHelper::siteUrl('meet-the-staff#' . $element->slug);
      } elseif ($element->section->handle == 'menu') {
        $href = UrlHelper::siteUrl('menu#' . $element->slug);
      } elseif ($element->url) {
        $href = $element->url;
      }

      return $href;
    }

    /**
     * https://craftcms.com/docs/plugins/variables
     * Output with {{ craft.supersearch.icon(params) }}
     */
    public function icon($slug, $size = '24')
    {
      if (!$slug) return false;

      $iconPath = self::getIcons($slug);

      if (!$iconPath) return false;

      return TemplateHelper::raw('<svg width="' . $size . 'px" height="' . $size . 'px" viewBox="0 0 24 24">
        <path fill="none" d="M0 0h24v24H0z"/>
        <path fill="currentColor" d="' . $iconPath . '" />
      </svg>');
    }

